import React, { useState } from 'react';
import './CreateEvents.css';

function CreateEvents() {
  return (
    <div className='CreateEventsMainContainer'>
      <div className='UploadEventForm'>
        <h2> Create Events </h2>
        <UploadEvent/>
      </div>
    </div>
  );
}

function UploadEvent() {
  const [file, setFile] = useState(null);
  const [description, setDescription] = useState('');

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setFile(null);
    setDescription('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="image-upload">Upload an Image:</label>
        <input type="file" id="image-upload" onChange={handleFileChange} accept="image/*" />
        <input type='text' placeholder='Enter the title of the Event' name='eventtittle'/>
      </div>
      <div>
        <textarea id="description" value={description} onChange={handleDescriptionChange} rows={10} cols={125} placeholder='Write event description'/>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
}


export default CreateEvents;
